import React from "react";
import { useState, useEffect } from "react";
import Header from "./Header.js";
import { BrowserRouter } from "react-router-dom";

function App() {

  const [eventLists, setEventLists] = useState([]);
  const pokeDB = "http://localhost:9292/pokemon";

  useEffect(() => {
    fetch(pokeDB)
      .then((res) => res.json())
      .then((data) => setEventLists(data));
  }, []);

  function handleEventDelete(name) {
    console.log("Delete button has been clicked!")
    const newPokemonCards = eventLists.filter((poke) => poke.name !== name);
    setEventLists(newPokemonCards);
  }

  function handleAdd(newPoke) {
    console.log("Submit button has been clicked!");
    const addPoke = [...eventLists, newPoke];
    setEventLists(addPoke);
  }
  return (
    <BrowserRouter>
      <div className="App">
        <Header />
      </div>
    </BrowserRouter>
  );
}

export default App;
